}
.TACdialog h1,
.TANdialog h1,
.cardTANdialog h1,
.Heurekadialog h1{
    margin:0;
    background:#aabacc;
    color:#fff;
    padding:1rem 2rem;
    font-size:1.6rem;
    font-weight:400;
}
.removewhite{
    display: inline-block;
    background-size: contain;
    background-repeat: no-repeat;
    background-image: url(data:image/png;base64,);
    background-image: url(data:image/svg+xml;base64,);
    float: right;
    padding-right:26px;
    background-position:center right;
    font-size:0.9rem;
    color:#fff;
    text-decoration:none;
}
.TACdialog > *,
.TANdialog > *,
.cardTANdialog > *,
.Heurekadialog > *{
    margin: 1rem 2rem;
}
.TACdialog select option{
    -webkit-appearance: menulist;
    padding:0;
    font-family: 'open_sansregular',Arial,sans-serif;
    font-size:1rem;
}
.TACdialog form,
.TANdialog form,
.cardTANdialog form,
.Heurekadialog form{
    margin:0;
    background:#e6ebf0;
    padding:1rem 2rem;
    text-align:center;
}
.TACdialog select,
.TACdialog input[type=number],
.cardTANdialog input[type=number],
.TANdialog input[type=number]{
    -webkit-appearance: menulist;
    padding:.3rem .5rem; 
    font-family: 'open_sansregular',Arial,sans-serif;
    font-size:1rem;
    margin-left:1rem;
    border:solid 1px #aabacc;
    border-radius:4px;
}
.TACdialog input[type=number],
.TANdialog input[type=number],
.cardTANdialog input[type=number]{
    -webkit-appearance: none;
    -moz-appearance:textfield;
}
.TACdialog .button,
.TANdialog .button,
.cardTANdialog .button,
.Heurekadialog .button{
    background: #0d5487;
    border: none;
    outline: none;
    color: #fff;
    border-radius: 3px;
    font-family: 'open_sansregular',Arial,sans-serif;
    font-size:1rem;
    padding:.5rem 1.5rem;
    margin-left:1rem;
    cursor:pointer;
}
.TACdialog .error,
.TANdialog .error,
.cardTANdialog .error{
    display: none;
}
body.error .TACdialog .error,
body.error .TANdialog .error,
body.error .cardTANdialog .error{
    background:#F9D2D2;
    border-radius: 3px;
    font-family: 'open_sansregular',Arial,sans-serif;
    font-size:1rem;
    padding:.5rem 1.5rem;
    margin-left:1rem;
    color:#9e0000;
    text-align:center;
    display: block;
}
.TACdialog .passed,
.TANdialog .passed,
.cardTANdialog .passed,
.Heurekadialog .passed{
    text-align:center;
    color:#19CE2E;
    font-size:1.3rem;
    margin-top:4rem;
    padding-top:3rem;
    padding-bottom:2rem;
    position:relative;
}
.TACdialog .passed:before,
.TANdialog .passed:before,
.cardTANdialog .passed:before,
.Heurekadialog .passed:before{
    background-image: url(data:image/png;base64,);
    background-image: url(data:image/svg+xml;base64,);
    content: " ";
    width: 3rem;
    height: 3rem;
    background-size: contain;
    background-repeat: no-repeat;
    position: absolute;
    top: -1rem;
    left:48%;
    left: calc(50% - 1.5rem);
}

