/************************************************************************
* FEDLOG-1791 Cookie Banner
************************************************************************/ 
.banner {
    color: #fff;
    background: #313a45;
    position: relative;
    z-index: 2;
}

.banner__inner {
    margin: 0 auto;
    text-align: left;
    overflow: hidden;
    padding: 1.5rem 4rem 1.5rem 2.4rem;
	max-width: 1170px;
}

.banner--cookie .banner__inner:before {
    content: "i";
}

.banner__inner:before {
    font-family: icons;
    font-size: 12px;
	font-weight:900;
    float: left;
    margin-left: -2.2rem;
    margin-right: .5rem;
    top: .1rem;
    position: relative;   
	background: #fff;
    border-radius: 100%;
    width: 18px;
    height: 18px;
    text-align: center;
}

.banner__close--cookie {
    width: auto;
    height: auto;
    float: right;
    margin-left: 1rem;
    margin-right: -4rem;
    position: static;
}

.banner__close {
    width: 2.2rem;
    height: 2.2rem;
    position: absolute;
    right: 1.5rem;
    top: 1.5rem;
    background: 0 0;
    border: 0;
    position: relative;
    cursor: pointer;
    display: inline-block;
    padding: 0;
    margin: 0;
    outline: 0;
    vertical-align: middle;
}

.banner-btn {
    width: auto;
    float: right;
    margin-left: 1rem;
    margin-right: -4rem;
    position: static;
    right: 1.5rem;
    color: #fff !important;
    background-color: #ff7900;
    border-top-color: #ff7900;
    border-bottom-color: #c66206;
    box-shadow: inset 0 -2px 0 0 #df6e07;
    padding: .5rem 1rem;
    display: inline-block;
    vertical-align: middle;
    text-align: center;
    font-weight: 900;
    line-height: 1;
    font-size: 1rem;
    border-radius: 4px;
    border: 0;
    border-top: 1px solid transparent;
    border-bottom: 1px solid transparent;
    cursor: pointer;
}
.banner-btn > span{
	color: #fff !important;
	font-weight:normal;
}

.banner__inner p {
    margin: 0;
	color: #fff;	
	font-size:15px;
}

.banner__inner p  > a{
   border-bottom: solid 2px transparent;
    transition: border-bottom .5s ease;
}

.banner__inner p  > a:hover{
	 border-bottom: solid 2px #fff;
	 transition: border-bottom .5s ease;
}

.banner__inner a {
    color: #fff;
    border-bottom-width: 2px;
    font-weight: 700;
    border-bottom: 2px solid transparent;
	text-decoration: none;
    transition: color .25s,border-color .25s;
    cursor: pointer;
}

.banner-wrapper {
    max-width: 120rem;
    margin: 0 auto;
    position: relative;
    padding-left: 1.5rem;
    padding-right: 1.5rem;
}

